import Hero from '../components/Hero';
import SectionCard from '../components/SectionCard';
import JobCard from '../components/JobCard';

const Home = () => {
  const jobs = [
    {
      title: "Senior React Developer",
      company: "TechCorp",
      location: "Boston, MA",
      salary: "$70K - $80K",
      description: "We are seeking a talented Front-End Developer to join our team in Boston, MA. The ideal candidate will have 3+ years of experience with React and modern JavaScript."
    },
    {
      title: "Front-End Engineer (React & Redux)",
      company: "StartupX",
      location: "Miami, FL",
      salary: "$70K - $80K",
      description: "Join our team as a Front-End Developer in sunny Miami, FL. We are looking for a motivated individual who can build scalable UIs with React and Redux."
    },
    {
      title: "React.js Developer",
      company: "DevStudio",
      location: "Brooklyn, NY",
      salary: "$70K - $80K",
      description: "Are you passionate about front-end development? Join our team in vibrant Brooklyn, NY, and work on cutting-edge projects using React.js."
    }
  ];

  return (
    <div>
      <Hero />
      <div className="py-10 px-6">
        <div className="flex flex-col md:flex-row gap-6 justify-center mb-10">
          <SectionCard
            title="For Developers"
            description="Browse our React jobs and start your career today"
            buttonLabel="Browse Jobs"
            onClick={() => window.location.href = '/jobs'}
          />
          <SectionCard
            title="For Employers"
            description="List your job to find the perfect developer for the role"
            buttonLabel="Add Job"
            onClick={() => window.location.href = '/add-job'}
          />
        </div>

        <h2 className="text-2xl font-bold text-center mb-6 text-indigo-600">Browse Jobs</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {jobs.map((job, index) => (
            <JobCard key={index} {...job} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
