// src/contexts/JobContext.jsx
import { createContext, useContext, useState } from 'react';

const JobContext = createContext();

export const useJobs = () => {
  const context = useContext(JobContext);
  if (!context) {
    throw new Error('useJobs must be used within a JobProvider');
  }
  return context;
};

export const JobProvider = ({ children }) => {
  const [jobs, setJobs] = useState([
    {
      id: 1,
      title: "Senior React Developer",
      company: "TechCorp",
      location: "Boston, MA",
      salary: "70K - 80K",
      description: "We are seeking a talented Front-End Developer to join our team in Boston, MA. The ideal candidate will have 3+ years of experience with React and modern JavaScript."
    }
  ]);

  const addJob = (job) => {
    const newJob = {
      ...job,
      id: Date.now(),
    };
    setJobs(prev => [newJob, ...prev]);
  };

  return (
    <JobContext.Provider value={{ jobs, addJob }}>
      {children}
    </JobContext.Provider>
  );
};
